item.sql:

select count(1) from
(select count(*) from Daily_Account_Snapshot t where 
                             {@t.CreateTime >= to_date($ST,'yyyy-MM-dd')}
                             {&@t.CreateTime < to_date($ET,'yyyy-MM-dd')+1}
                             {&@t.DownChannelNo}
                             {&@t.CardType}
                             {&@t.PayType}
                             {&@t.BusinessType}
group by t.create_time,t.down_channel_no,t.card_type,t.pay_type,t.business_type)
