item.sql:

SELECT d.*
  FROM (SELECT tab.*, ROWNUM rn
          FROM (SELECT *
                 FROM SYS_DICTIONARY t
                 where
                 {:Name}
                 {&:Type}
                 order by t.short desc) tab) d
 WHERE rn BETWEEN ({ :PI} - 1) * { :PS} + 1 AND { :PI} * { :PS}


select count(1) from SYS_DICTIONARY t
                        where 
                         {:Name}
                         {&:Type}
